    public static JsonNode sendSimpleMessage(String email) {
        System.out.println("Email :: "+email);
        try {
            HttpResponse<JsonNode> request = Unirest.
                    post("https://api.mailgun.net/v3/sandboxe37b0558f7ad4405a96f9d2c57656a7c.mailgun.org/messages")
                    .basicAuth("api", "42fa0849e91c07cc38d49a2027a14d4f-7fba8a4e-539c11b1")
                    .queryString("from", "ISKCON postmaster@sandboxe37b0558f7ad4405a96f9d2c57656a7c.mailgun.org")
                    .queryString("to", email)
                    .queryString("subject", "INNER DEVELOPMENT THROUGH VEDIC WISDOM || ISKCON YOUTH FORUM ")
                    .queryString("template", "iyf")
                    .asJson();
            return request.getBody();
        }catch (UnirestException e){
            System.out.println("Exception for email :::  "+email);
            System.out.println(e.getMessage());
            return null;
        }
    }
